package com.nxt.ott.activity;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.FragmentActivity;import android.support.v4.app.FragmentManager;import android.view.View;import android.widget.RelativeLayout;import android.widget.TextView;import com.nxt.ott.MyApplication;import com.nxt.ott.R;import com.nxt.ott.fragment.SupplyDemandFragment;/** * Created by cyjia on 2016/7/20. */public class SupplyActivity extends FragmentActivity {    private SupplyDemandFragment supplyDemandFragment;    private FragmentManager fragmentManager;    public MyApplication myApplication;    private TextView tvTitle;    private RelativeLayout rlLeft;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        MyApplication.getInstance().addActivity(this);        setContentView(R.layout.activity_supply);        initview();    }    private void initview() {        rlLeft = (RelativeLayout) findViewById(R.id.layout_left);        rlLeft.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                switch (v.getId()){                    case R.id.layout_left:                        finish();                        break;                }            }        });        tvTitle = (TextView) findViewById(R.id.tv_title);        tvTitle.setText(getString(R.string.agrotechnique_supplymsg));        fragmentManager = getSupportFragmentManager();        supplyDemandFragment = new SupplyDemandFragment();        fragmentManager.beginTransaction().replace(R.id.layout_content, supplyDemandFragment).commit();    }}