package com.nxt.ott.activity.convenience;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import com.nxt.ott.R;import com.nxt.ott.activity.WebActivity;import com.nxt.ott.base.BaseZoomTitleActivity;import com.nxt.ott.Constant;/** * Created by iwon on 2016/6/24. */public class MallChooseActivity extends BaseZoomTitleActivity {    private View contentview;    private String url,title;    @Override    protected void initView() {        application.addActivity(this);        initTopbar(this, getString(R.string.mall));        contentview= LayoutInflater.from(this).inflate(R.layout.activity_mall_choose, null);        parentlayout.addView(contentview);        contentview.findViewById(R.id.layout_mall_materials).setOnClickListener(this);        contentview.findViewById(R.id.layout_mall_department).setOnClickListener(this);    }    @Override    protected int getLayout() {        return R.layout.common_scrollview;    }    @Override    public void onClick(View v) {        switch (v.getId()){            case R.id.layout_mall_materials:                url= Constant.MALL_MATERIALS_URL;                title=getString(R.string.mall_materials);                break;            case R.id.layout_mall_department:                url= Constant.MALL_DEPARTMENT_URL;                title=getString(R.string.mall_department);                break;        }        startActivity(new Intent(this,WebActivity.class).putExtra("title",title).putExtra("url",url));        super.onClick(v);    }}