package com.nxt.ott.activity.convenience;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import com.nxt.ott.R;import com.nxt.ott.activity.WebActivity;import com.nxt.ott.base.BaseZoomTitleActivity;import com.nxt.ott.Constant;/** * Created by iwon on 2016/6/24. */public class PhoneBillActivity extends BaseZoomTitleActivity {    private String url,title;    private View contentview;    @Override    protected void initView() {        application.addActivity(this);        initTopbar(this, getString(R.string.recharge));        contentview= LayoutInflater.from(this).inflate(R.layout.activity_phone_bill, null);        parentlayout.addView(contentview);        contentview.findViewById(R.id.layout_phone_bill_mobile).setOnClickListener(this);        contentview.findViewById(R.id.layout_phone_bill_unicom).setOnClickListener(this);        contentview.findViewById(R.id.layout_phone_bill_telecom).setOnClickListener(this);        contentview.findViewById(R.id.layout_phone_bill_yns).setOnClickListener(this);    }    @Override    protected int getLayout() {        return R.layout.common_scrollview;    }    @Override    public void onClick(View v) {        switch (v.getId()){            case R.id.layout_phone_bill_mobile:                url= Constant.SERVICE_PHONE_BILL_MOBILE_URL;                title=getString(R.string.mobile);                break;            case R.id.layout_phone_bill_unicom:                url= Constant.SERVICE_PHONE_BILL_UNICOM_URL;                title=getString(R.string.unicom);                break;            case R.id.layout_phone_bill_telecom:                url= Constant.SERVICE_PHONE_BILL_TELECOM_URL;                title=getString(R.string.telecom);                break;            case R.id.layout_phone_bill_yns:                url= Constant.SERVICE_PHONE_BILL_YNS_URL;                title=getString(R.string.yns_bill);                break;        }        startActivity(new Intent(this,WebActivity.class).putExtra("title",title).putExtra("url",url));        super.onClick(v);    }}